export { default as useAsync } from './use-async';
export { default as useGenerator } from './use-async'; // will be deprecated in the next major version

export { default as Provider } from './provider';
export { default as store } from './store';
export var useReducer = function useReducer() {
  store.useReducer.apply(store, arguments);
  store.update();
};
export var useMiddleware = function useMiddleware() {
  return store.useMiddleware.apply(store, arguments);
};
export var queryGlobalState = function queryGlobalState(query) {
